  // mock request
  if (req.enforcer.mockMode) {
    const data = await req.enforcer.mockStore.getData(req, res)
    const list = data.list || []
    res.send(list)
  } else {
    // code here for non-mock
  }
})

app.post('/', async (req, res) => {
  // mock request
  if (req.enforcer.mockMode) {
    const body = req.body
    await req.enforcer.mockStore.setData(req, res, body)
    res.sendStatus(201)
  } else {
    // code here for non-mock
  }
})
